import React, { Component } from 'react'
import { Link ,Route,Switch} from 'react-router-dom'
import Detail from './Detail'

export default class Message extends Component {
  state={
    megs: [{ id: '01', title: 'detail1' },
      { id: '02', title: 'detail2' },
      { id: '03', title: 'detail3' }]
  }
  render() {
    const {megs} = this.state
    return (
      <div>
        <ul>
          {
            megs.map((item)=>{
              return (
               <li key={item.id}>
                  {/* 路由传参 params */}
                  {/* <Link to={`/home/messages/detail/${item.id}/${item.title}`}>{item.title}</Link> */}

                  {/* 向路由传递search参数 */}
                  <Link to={`/home/messages/detail/?id=${item.id}&${item.title}`}>{item.title}</Link>
               </li>
              )
            })
          }
        </ul>
        <Switch>
          {/* 声明接收params参数 */}
          {/* <Route path='/home/messages/detail/:id/:title' component={Detail}></Route> */}

          {/* search 参数  无需声明接收*/}
          <Route path="/home/messages/detail" component={Detail}></Route>

        </Switch>

      </div>
    )
  }
}
