import React, { Component } from 'react'
import { Select, Button, Space } from 'antd'

export default class Count extends Component {
  state ={
    options:[
        {value:1},
        {value:2},
        {value:3}
    ]
  }
   handleChange = (value) => {
        console.log(`selected ${value}`);
    };
  render() {
    return (
      <div>
        <h1>当前和为：</h1>
        <Select defaultValue="1" style={{width:120}} onChange={this.handleChange} options={this.state.options}>
        </Select>
            <Space  >
                <Button>+</Button>
                <Button>-</Button>
                <Button>和为奇数就加</Button>
                <Button>异步加</Button>
            </Space>

      </div>
    )
  }
}
